mapper.select:
select ClassInfo.id, ClassInfo.reservation_cnt, ClassType.class_type_name, ClassType.class_name, ClassPoint.class_point_name, ClassPoint.class_point_maximum, InstInfo.instructor_name, ClassInfo.class_date, ClassTime.class_start_time, ClassTime.class_end_time, ClassType.class_memo, ClassInfo.create_dt, ClassInfo.update_dt, ClassInfo.updated, CASE WHEN ReservationInfo.user_id IS NOT NULL THEN 1 ELSE 0 END as isReserved from class_info as ClassInfo inner join class_type as ClassType on ClassInfo.class_type_id = ClassType.id inner join class_point as ClassPoint on ClassInfo.class_point_id = ClassPoint.id inner join class_time as ClassTime on ClassInfo.class_time_id = ClassTime.id inner join instructor_info as InstInfo on ClassInfo.instructor_id = InstInfo.id left join user_reservation as ReservationInfo on ClassInfo.id = ReservationInfo.class_id and ReservationInfo.user_id = #{userId} and ReservationInfo.cancel_flag != 1
STR_TO_DATE(CONCAT(class_date, ' ', class_start_time), '%Y-%m-%d %H:%i') > NOW()
AND class_date = #{searchClassDate}
AND class_name = #{searchClassName}
AND instructor_name = #{searchInstructor}
ORDER BY class_date asc, class_start_time asc LIMIT #{size} OFFSET #{offset}

SELECT 1
from class_info as ClassInfo
inner join class_type as ClassType on ClassInfo.class_type_id = ClassType.id
inner join class_point as ClassPoint on ClassInfo.class_point_id = ClassPoint.id
inner join class_time as ClassTime on ClassInfo.class_time_id = ClassTime.id
inner join instructor_info as InstInfo on ClassInfo.instructor_id = InstInfo.id

    STR_TO_DATE(CONCAT(class_date, ' ', class_start_time), '%Y-%m-%d %H:%i') > NOW()


        AND class_date = #{searchClassDate}



        AND class_name = #{searchClassName}



        AND instructor_name = #{searchInstructor}



LIMIT 1 OFFSET #{offset}
